import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';

interface MigrationSuccessDetail {
  recordsClaimed: number;
  tableStats: Record<string, number>;
  timestamp: string;
}

interface MigrationSuccessBannerProps {
  onDismiss?: () => void;
}

const MigrationSuccessBanner: React.FC<MigrationSuccessBannerProps> = ({ onDismiss }) => {
  const { t } = useTranslation(['auth', 'common']);
  const [migrationData, setMigrationData] = useState<MigrationSuccessDetail | null>(null);
  const [isVisible, setIsVisible] = useState(false);

  const handleDismiss = () => {
    setIsVisible(false);
    onDismiss?.();
    
    // Clear data after animation
    setTimeout(() => {
      setMigrationData(null);
    }, 300);
  };

  useEffect(() => {
    const handleMigrationSuccess = (event: CustomEvent<MigrationSuccessDetail>) => {
      setMigrationData(event.detail);
      setIsVisible(true);
      
      // Auto-dismiss after 8 seconds
      setTimeout(() => {
        handleDismiss();
      }, 8000);
    };

    window.addEventListener('data-migration-success', handleMigrationSuccess as EventListener);

    return () => {
      window.removeEventListener('data-migration-success', handleMigrationSuccess as EventListener);
    };
  }, [handleDismiss]);

  const formatRecordTypes = (tableStats: Record<string, number>): string => {
    const nonZeroTables = Object.entries(tableStats)
      .filter(([, count]) => count > 0)
      .sort(([, a], [, b]) => b - a) // Sort by count descending
      .map(([table, count]) => {
        // Map internal table names to user-friendly names
        const friendlyNames: Record<string, string> = {
          exercises: t('auth:migration.exercises'),
          activityLogs: t('auth:migration.workouts'),
          userPreferences: t('auth:migration.preferences'),
          appSettings: t('auth:migration.settings'),
          workouts: t('auth:migration.workoutPlans'),
          workoutSessions: t('auth:migration.sessions')
        };
        
        return `${count} ${friendlyNames[table] || table}`;
      });

    if (nonZeroTables.length === 0) {
      return '';
    } else if (nonZeroTables.length === 1) {
      return nonZeroTables[0];
    } else if (nonZeroTables.length === 2) {
      return nonZeroTables.join(` ${t('common:and')} `);
    } else if (nonZeroTables.length <= 4) {
      const last = nonZeroTables.pop();
      return `${nonZeroTables.join(', ')}, ${t('common:and')} ${last}`;
    } else {
      // For very long lists, show top 3 and summarize the rest
      const shown = nonZeroTables.slice(0, 3);
      const remaining = nonZeroTables.length - 3;
      return `${shown.join(', ')}, ${t('common:and')} ${remaining} ${t('auth:migration.others')}`;
    }
  };

  if (!migrationData || !isVisible) {
    return null;
  }

  return (
    <div 
      className={`fixed top-4 left-4 right-4 z-50 transform transition-all duration-300 ease-out ${
        isVisible ? 'translate-y-0 opacity-100' : '-translate-y-full opacity-0'
      }`}
      role="alert"
      aria-live="polite"
    >
      <div className="bg-green-50 border border-green-200 rounded-lg shadow-lg p-4 mx-auto max-w-sm sm:max-w-md md:max-w-lg lg:max-w-xl">
        <div className="flex items-start">
          <div className="flex-shrink-0" aria-hidden="true">
            <div className="w-6 h-6 text-green-600">
              âœ…
            </div>
          </div>
          
          <div className="ml-3 flex-1 min-w-0">
            <h3 className="text-sm sm:text-base font-medium text-green-800 break-words">
              {t('auth:migration.success.title')}
            </h3>
            
            <div className="mt-2 text-sm sm:text-base text-green-700 space-y-2">
              <p className="break-words">
                {t('auth:migration.success.message', { 
                  count: migrationData.recordsClaimed 
                })}
              </p>
              
              {migrationData.recordsClaimed > 0 && (
                <div className="text-xs sm:text-sm bg-green-100/50 rounded-md p-2 break-words">
                  <span className="font-medium">{t('auth:migration.success.details')}:</span>{' '}
                  <span>{formatRecordTypes(migrationData.tableStats)}</span>
                </div>
              )}
            </div>
          </div>
          
          <div className="ml-4 flex-shrink-0 self-start">
            <button
              type="button"
              className="bg-green-50 rounded-md p-1.5 sm:p-2 text-green-400 hover:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-600 focus:ring-offset-2 focus:ring-offset-green-50 transition-colors"
              onClick={handleDismiss}
              aria-label={t('common:dismiss')}
            >
              <span className="sr-only">{t('common:dismiss')}</span>
              <svg className="h-3 w-3 sm:h-4 sm:w-4" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MigrationSuccessBanner;

import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useAuth } from '../hooks/useAuth';
import { AuthModal } from './auth/AuthModal';

interface ProfileSectionProps {
  onViewProfile?: () => void;
  className?: string;
}

export const ProfileSection: React.FC<ProfileSectionProps> = ({
  onViewProfile,
  className = ''
}) => {
  const { t } = useTranslation(['auth', 'common']);
  const { isAuthenticated, user, signOut, loading } = useAuth();
  const [showAuthModal, setShowAuthModal] = useState(false);

  const handleSignOut = async () => {
    await signOut();
  };

  const getInitials = (name?: string, email?: string) => {
    if (name) {
      return name
        .split(' ')
        .map(word => word.charAt(0))
        .join('')
        .toUpperCase()
        .slice(0, 2);
    }
    if (email) {
      return email.charAt(0).toUpperCase();
    }
    return '?';
  };

  const displayName = user?.displayName || user?.email?.split('@')[0] || t('profile.anonymous');
  const initials = getInitials(user?.displayName, user?.email);

  return (
    <>
      <div className={`bg-white dark:bg-gray-800 rounded-lg shadow-lg p-4 mb-4 ${className}`}>
        <h2 className="text-lg font-semibold text-gray-900 dark:text-gray-100 mb-3 flex items-center gap-2">
          <svg className="h-5 w-5 text-blue-600 dark:text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
          </svg>
          {t('profile.title', 'Profile')}
        </h2>

      {isAuthenticated && user ? (
        <div className="space-y-3">
          {/* User Info */}
          <div className="flex items-center space-x-3 p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
            {user.avatarUrl ? (
              <img
                src={user.avatarUrl}
                alt={displayName}
                className="h-10 w-10 rounded-full object-cover"
              />
            ) : (
              <div className="h-10 w-10 rounded-full bg-blue-600 dark:bg-blue-700 flex items-center justify-center text-white text-sm font-medium">
                {initials}
              </div>
            )}
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-gray-900 dark:text-white truncate">
                {displayName}
              </p>
              {user.email && (
                <p className="text-sm text-gray-500 dark:text-gray-400 truncate">
                  {user.email}
                </p>
              )}
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex space-x-3">
            <button
              type="button"
              onClick={onViewProfile}
              className="flex-1 flex items-center justify-center gap-2 py-2 px-4 bg-blue-600 hover:bg-blue-700 text-white font-medium rounded-lg transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
              </svg>
              {t('profile.viewProfile', 'View Profile')}
            </button>
            
            <button
              type="button"
              onClick={handleSignOut}
              disabled={loading}
              className="flex-1 flex items-center justify-center gap-2 py-2 px-4 bg-gray-600 hover:bg-gray-700 disabled:bg-gray-400 disabled:cursor-not-allowed text-white font-medium rounded-lg transition-colors focus:outline-none focus:ring-2 focus:ring-gray-500"
            >
              <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
              </svg>
              {loading ? t('common:loading') : t('signOut')}
            </button>
          </div>
        </div>
      ) : (
        <div className="space-y-3">
          {/* Not signed in message */}
          <div className="p-3 bg-gray-50 dark:bg-gray-700 rounded-lg text-center">
            <p className="text-sm text-gray-600 dark:text-gray-300 mb-2">
              {t('profile.notSignedIn', 'You are not signed in')}
            </p>
            <p className="text-xs text-gray-500 dark:text-gray-400">
              {t('profile.signInToSync', 'Sign in to sync your data across devices')}
            </p>
          </div>

          {/* Sign In Button */}
          <button
            type="button"
            onClick={() => setShowAuthModal(true)}
            className="w-full flex items-center justify-center gap-2 py-2 px-4 bg-blue-600 hover:bg-blue-700 text-white font-medium rounded-lg transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 16l-4-4m0 0l4-4m0 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
            </svg>
            {t('signIn.button')}
          </button>
        </div>
      )}
      </div>

      {/* Auth Modal */}
      <AuthModal
        isOpen={showAuthModal}
        onClose={() => setShowAuthModal(false)}
        initialMode="signin"
      />
    </>
  );
};

import React from 'react';
import { useOfflineStatus } from '../hooks/useOfflineStatus';
import { useNetworkSync } from '../hooks/useNetworkSync';
import { useAuth } from '../hooks/useAuth';

/**
 * SyncStatusBanner component shows connectivity and sync status
 * Extends OfflineBanner with sync capabilities
 */
const SyncStatusBanner: React.FC = () => {
  const { isOffline, isOnline, hasBeenOffline } = useOfflineStatus();
  const { state: syncState, actions: syncActions } = useNetworkSync();
  const { isAuthenticated } = useAuth();

  // Helper to format time ago
  const formatTimeAgo = (timestamp?: number) => {
    if (!timestamp) return 'Never';
    const minutes = Math.floor((Date.now() - timestamp) / 60000);
    if (minutes < 1) return 'Just now';
    if (minutes < 60) return `${minutes}m ago`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h ago`;
    return `${Math.floor(hours / 24)}d ago`;
  };

  // Show sync error state (highest priority)
  if (isAuthenticated && syncState.errors.length > 0) {
    return (
      <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-3 mb-4" role="alert" data-testid="sync-status-banner">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <div className="flex-shrink-0" aria-hidden="true">
              âš ï¸
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium">
                Sync failed
              </p>
              <p className="text-xs mt-1">
                {syncState.errors[0]} â€¢ Last attempt: {formatTimeAgo(syncState.lastSyncAttempt)}
              </p>
            </div>
          </div>
          <div className="flex space-x-2">
            <button
              onClick={syncActions.clearErrors}
              className="text-xs px-2 py-1 bg-red-200 text-red-800 rounded hover:bg-red-300 transition-colors"
              aria-label="Dismiss error"
            >
              Dismiss
            </button>
            {isOnline && (
              <button
                onClick={syncActions.retry}
                className="text-xs px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
                aria-label="Retry sync"
              >
                Retry
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  // Show sync in progress (for authenticated users)
  if (isAuthenticated && syncState.isSyncing) {
    return (
      <div className="bg-blue-100 border-l-4 border-blue-500 text-blue-700 p-3 mb-4" role="status" data-testid="sync-status-banner">
        <div className="flex items-center">
          <div className="flex-shrink-0" aria-hidden="true">
            <div className="w-4 h-4 border-2 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
          </div>
          <div className="ml-3">
            <p className="text-sm font-medium">
              Syncing data...
            </p>
            <p className="text-xs mt-1">
              Backing up your progress to the cloud
            </p>
          </div>
        </div>
      </div>
    );
  }

  // Show offline message (highest priority for non-authenticated users)
  if (isOffline) {
    return (
      <div className="bg-orange-100 border-l-4 border-orange-500 text-orange-700 p-3 mb-4" role="alert" data-testid="sync-status-banner">
        <div className="flex items-center">
          <div className="flex-shrink-0" aria-hidden="true">
            ðŸ“´
          </div>
          <div className="ml-3">
            <p className="text-sm font-medium">
              You're offline
            </p>
            <p className="text-xs mt-1">
              {isAuthenticated 
                ? "All features work normally. Data will sync when reconnected."
                : "All features work normally. Your data is saved locally."
              }
            </p>
          </div>
        </div>
      </div>
    );
  }

  // Show reconnection message briefly (for all users)
  if (isOnline && hasBeenOffline) {
    return (
      <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-3 mb-4" role="alert" data-testid="sync-status-banner">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <div className="flex-shrink-0" aria-hidden="true">
              ðŸŒ
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium">
                Connection restored
              </p>
              <p className="text-xs mt-1">
                {isAuthenticated 
                  ? "Back online! Data will sync automatically."
                  : "Back online! All features available."
                }
              </p>
            </div>
          </div>
          {isAuthenticated && syncState.hasChangesToSync && (
            <button
              onClick={syncActions.triggerSync}
              className="text-xs px-2 py-1 bg-green-600 text-white rounded hover:bg-green-700 transition-colors ml-3"
              aria-label="Sync now"
            >
              Sync Now
            </button>
          )}
        </div>
      </div>
    );
  }

  // Show pending changes indicator (for authenticated users only)
  if (isAuthenticated && isOnline && syncState.hasChangesToSync && !syncState.isSyncing) {
    const lastSyncText = formatTimeAgo(syncState.lastSuccessfulSync);
    
    return (
      <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-3 mb-4" role="status" data-testid="sync-status-banner">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <div className="flex-shrink-0" aria-hidden="true">
              ðŸ”„
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium">
                Changes ready to sync
              </p>
              <p className="text-xs mt-1">
                Last synced: {lastSyncText}
              </p>
            </div>
          </div>
          <button
            onClick={syncActions.triggerSync}
            className="text-xs px-3 py-1 bg-yellow-600 text-white rounded hover:bg-yellow-700 transition-colors"
            aria-label="Sync changes now"
          >
            Sync
          </button>
        </div>
      </div>
    );
  }

  // No banner when online and no sync issues
  return null;
};

