                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  {t('redirectingToProvider', 'Redirecting to Google...')}
                </>
              ) : (
                <>
                  <span className="mr-2">ðŸ”</span> {/* i18n-exempt: Google search icon */}
                  {t('continueWithGoogle')}
                </>
              )}
            </button>

            <button
              type="button"
              onClick={onSwitchToMagicLink}
              disabled={loading}
              className="w-full inline-flex justify-center py-2 px-4 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm bg-white dark:bg-gray-700 text-sm font-medium text-gray-500 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-600 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              { }
              <span className="mr-2">âœ‰ï¸</span> {/* i18n-exempt: universal email icon */}
              {t('continueWithEmail')}
            </button>
          </div>
        </div>

        <div className="mt-6 text-center">
          <button
            type="button"
            onClick={onSwitchToSignIn}
            disabled={loading}
            className="text-sm text-blue-600 dark:text-blue-400 hover:text-blue-500 dark:hover:text-blue-300 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {t('signUp.switchToSignIn')}
          </button>
        </div>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useAuth } from '../../hooks/useAuth';

interface UserProfileProps {
  className?: string;
}

export const UserProfile: React.FC<UserProfileProps> = ({ className = '' }) => {
  const { t } = useTranslation(['auth', 'common']);
  const { user, signOut, loading } = useAuth();
  const [showDropdown, setShowDropdown] = useState(false);

  if (!user) return null;

  const handleSignOut = async () => {
    setShowDropdown(false);
    await signOut();
  };

  const getInitials = (name?: string, email?: string) => {
    if (name) {
      return name
        .split(' ')
        .map(word => word.charAt(0))
        .join('')
        .toUpperCase()
        .slice(0, 2);
    }
    if (email) {
      return email.charAt(0).toUpperCase();
    }
    return '?';
  };

  const displayName = user.displayName || user.email?.split('@')[0] || t('profile.anonymous');
  const initials = getInitials(user.displayName, user.email);

  return (
    <div className={`relative ${className}`}>
      <button
        type="button"
        onClick={() => setShowDropdown(!showDropdown)}
        className="flex items-center space-x-2 text-gray-700 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white focus:outline-none focus:ring-2 focus:ring-blue-500 rounded-md p-1"
        aria-expanded={showDropdown}
        aria-haspopup="true"
      >
        {user.avatarUrl ? (
          <img
            src={user.avatarUrl}
            alt={displayName}
            className="h-8 w-8 rounded-full object-cover"
          />
        ) : (
          <div className="h-8 w-8 rounded-full bg-blue-600 dark:bg-blue-700 flex items-center justify-center text-white text-sm font-medium">
            {initials}
          </div>
        )}
        <span className="text-sm font-medium truncate max-w-24">{displayName}</span>
        <svg
          className={`h-4 w-4 transform transition-transform ${showDropdown ? 'rotate-180' : ''}`}
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
        </svg>
      </button>

      {showDropdown && (
        <>
          {/* Backdrop to close dropdown */}
          <div
            className="fixed inset-0 z-10"
            onClick={() => setShowDropdown(false)}
          />
          
          {/* Dropdown menu */}
          <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-md shadow-lg z-20 border border-gray-200 dark:border-gray-700">
            <div className="py-1">
              <div className="px-4 py-2 border-b border-gray-200 dark:border-gray-700">
                <p className="text-sm font-medium text-gray-900 dark:text-white truncate">
                  {displayName}
                </p>
                {user.email && (
                  <p className="text-sm text-gray-500 dark:text-gray-400 truncate">
                    {user.email}
                  </p>
                )}
              </div>
              
              <button
                type="button"
                onClick={() => {
                  setShowDropdown(false);
                  // TODO: Open profile settings modal
                }}
                className="block w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 focus:outline-none focus:bg-gray-100 dark:focus:bg-gray-700"
              >
                {t('profile.settings')}
              </button>
              
              <button
                type="button"
                onClick={handleSignOut}
                disabled={loading}
                className="block w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 focus:outline-none focus:bg-gray-100 dark:focus:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? t('common:loading') : t('signOut')}
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

import React, { useState } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { supabase } from '../../config/supabase';

interface DataExportButtonProps {
  className?: string;
  variant?: 'primary' | 'secondary';
}

const DataExportButton: React.FC<DataExportButtonProps> = ({ 
  className = '', 
  variant = 'secondary' 
}) => {
  const [isExporting, setIsExporting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);
  const { session } = useAuth();

  const handleExportData = async () => {
    setIsExporting(true);
    setError(null);
    setSuccess(false);

    try {
      if (!session?.access_token) {
        throw new Error('Authentication required');
      }

      const response = await supabase.functions.invoke('export-data', {
        headers: {
          Authorization: `Bearer ${session.access_token}`,
        },
      });

      if (response.error) {
        throw new Error(response.error.message || 'Export failed');
      }

      // Create and trigger download
      const blob = new Blob([JSON.stringify(response.data, null, 2)], {
        type: 'application/json'
      });
      
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `repcue-data-export-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      setSuccess(true);
      setTimeout(() => setSuccess(false), 5000);

    } catch (err) {
      console.error('Data export failed:', err);
      setError(err instanceof Error ? err.message : 'Export failed');
    } finally {
      setIsExporting(false);
    }
  };

  const baseClasses = variant === 'primary' 
    ? 'bg-blue-600 hover:bg-blue-700 text-white'
    : 'bg-gray-200 hover:bg-gray-300 text-gray-900';

  return (
    <div className="space-y-2">
      <button
        onClick={handleExportData}
        disabled={isExporting}
        className={`px-4 py-2 rounded-md font-medium transition-colors disabled:opacity-50 disabled:cursor-not-allowed ${baseClasses} ${className}`}
        aria-label="Export your data"
      >
        {isExporting ? 'Exporting...' : 'Export My Data'}
      </button>

      {error && (
        <div className="text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2" role="alert">
          {error}
        </div>
      )}

      {success && (
        <div className="text-sm text-green-600 bg-green-50 border border-green-200 rounded-md p-2" role="status">
          Your data has been exported successfully!
        </div>
      )}

      <div className="text-xs text-gray-500">
        <p>Export includes your workouts, exercises, settings, and activity logs.</p>
        <p>You can request up to 3 exports per day.</p>
      </div>
    </div>
  );
};

export default DataExportButton;

import React, { useState } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { supabase } from '../../config/supabase';

interface DeleteAccountModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: () => void;
}

const DeleteAccountModal: React.FC<DeleteAccountModalProps> = ({ 
  isOpen, 
  onClose, 
  onSuccess 
}) => {
  const [step, setStep] = useState<'confirm' | 'reason' | 'final'>('confirm');
  const [confirmation, setConfirmation] = useState('');
  const [reason, setReason] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { session, signOut } = useAuth();

  const handleClose = () => {
    if (!isDeleting) {
      setStep('confirm');
      setConfirmation('');
      setReason('');
      setError(null);
      onClose();
    }
  };

  const handleDeleteAccount = async () => {
    if (confirmation !== 'DELETE') {
      setError('You must type "DELETE" to confirm');
      return;
    }

    setIsDeleting(true);
    setError(null);

    try {
      if (!session?.access_token) {
        throw new Error('Authentication required');
      }

      const response = await supabase.functions.invoke('delete-account', {
        body: {
          confirmation: 'DELETE',
          reason: reason.trim() || undefined
        },
        headers: {
          Authorization: `Bearer ${session.access_token}`,
          'Content-Type': 'application/json'
        },
      });

      if (response.error) {
        throw new Error(response.error.message || 'Account deletion failed');
      }

      // Sign out user
      await signOut();
      
      onSuccess?.();
      handleClose();

    } catch (err) {
      console.error('Account deletion failed:', err);
      setError(err instanceof Error ? err.message : 'Account deletion failed');
    } finally {
      setIsDeleting(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-md w-full p-6 max-h-[90vh] overflow-y-auto">
        {step === 'confirm' && (
          <>
            <h2 className="text-xl font-bold text-red-600 mb-4">Delete Account</h2>
            <div className="space-y-4">
              <div className="bg-red-50 border border-red-200 rounded-md p-4">
                <h3 className="font-semibold text-red-800 mb-2">âš ï¸ This action cannot be undone</h3>
                <p className="text-red-700 text-sm">
                  Deleting your account will permanently remove all your data including:
                </p>
                <ul className="list-disc list-inside text-red-700 text-sm mt-2 space-y-1">
                  <li>All workout history and progress</li>
                  <li>Custom exercises and routines</li>
                  <li>Personal settings and preferences</li>
                  <li>Activity logs and statistics</li>
                </ul>
              </div>

              <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
                <h3 className="font-semibold text-yellow-800 mb-2">30-Day Grace Period</h3>
                <p className="text-yellow-700 text-sm">
                  Your account will be marked for deletion with a 30-day grace period. 
                  You can reactivate it by logging in during this time.
                </p>
              </div>

              <div className="flex space-x-3">
                <button
                  onClick={() => setStep('reason')}
                  className="flex-1 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md font-medium transition-colors"
                >
                  Continue
                </button>
                <button
                  onClick={handleClose}
                  className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-900 px-4 py-2 rounded-md font-medium transition-colors"
                >
                  Cancel
                </button>
              </div>
            </div>
          </>
        )}

        {step === 'reason' && (
          <>
            <h2 className="text-xl font-bold text-red-600 mb-4">Why are you leaving?</h2>
            <div className="space-y-4">
              <p className="text-gray-700 text-sm">
                Your feedback helps us improve. This information is optional and anonymous.
              </p>
              
              <textarea
                value={reason}
                onChange={(e) => setReason(e.target.value)}
                placeholder="Tell us why you're deleting your account (optional)"
                className="w-full p-3 border border-gray-300 rounded-md resize-none"
                rows={4}
                maxLength={500}
              />
              
              <div className="text-xs text-gray-500">
                {reason.length}/500 characters
              </div>

              <div className="flex space-x-3">
                <button
                  onClick={() => setStep('final')}
                  className="flex-1 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md font-medium transition-colors"
                >
                  Continue
                </button>
                <button
                  onClick={() => setStep('confirm')}
                  className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-900 px-4 py-2 rounded-md font-medium transition-colors"
                >
                  Back
                </button>
              </div>
            </div>
          </>
        )}

        {step === 'final' && (
          <>
            <h2 className="text-xl font-bold text-red-600 mb-4">Final Confirmation</h2>
            <div className="space-y-4">
              <p className="text-gray-700">
                Type <strong>DELETE</strong> to confirm you want to permanently delete your account:
              </p>
              
              <input
                type="text"
                value={confirmation}
                onChange={(e) => {
                  setConfirmation(e.target.value);
                  setError(null);
                }}
                placeholder="Type DELETE here"
                className="w-full p-3 border border-gray-300 rounded-md font-mono"
                autoFocus
                disabled={isDeleting}
              />

              {error && (
                <div className="text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2" role="alert">
                  {error}
                </div>
              )}

              <div className="flex space-x-3">
                <button
                  onClick={handleDeleteAccount}
                  disabled={isDeleting || confirmation !== 'DELETE'}
                  className="flex-1 bg-red-600 hover:bg-red-700 disabled:bg-red-400 text-white px-4 py-2 rounded-md font-medium transition-colors disabled:cursor-not-allowed"
                >
                  {isDeleting ? 'Deleting Account...' : 'Delete My Account'}
                </button>
                <button
                  onClick={handleClose}
                  disabled={isDeleting}
                  className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-900 px-4 py-2 rounded-md font-medium transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Cancel
                </button>
              </div>

              <p className="text-xs text-gray-500 text-center">
                This action will immediately sign you out and begin the deletion process.
              </p>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default DeleteAccountModal;

import React, { useEffect, useState } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { supabase } from '../config/supabase';
import { Routes } from '../types';

/**
 * OAuth callback page that handles authentication redirects
 * This page is typically visited after OAuth sign-in (Google, Apple, etc.)
 * Enhanced with better error handling and user feedback
 */
const AuthCallbackPage: React.FC = () => {
  const { t } = useTranslation('auth');
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<boolean>(false);
  const [provider, setProvider] = useState<string>('OAuth provider');

  useEffect(() => {
    const handleAuthCallback = async () => {
      try {
        // Extract provider from URL params for better UX
        const providerParam = searchParams.get('provider') || 'OAuth provider';
        setProvider(providerParam.charAt(0).toUpperCase() + providerParam.slice(1));

        // Check for OAuth error in URL params (common OAuth error pattern)
        const errorParam = searchParams.get('error');
        const errorDescription = searchParams.get('error_description');

        if (errorParam) {
          let friendlyMessage = errorDescription || errorParam;

          // Provide user-friendly error messages for common OAuth errors
          switch (errorParam) {
            case 'access_denied':
              friendlyMessage = t('errors.oauthAccessDenied', 'Sign-in was cancelled. You can try signing in again.');
              break;
            case 'invalid_request':
              friendlyMessage = t('errors.oauthInvalidRequest', 'There was a problem with the sign-in request. Please try again.');
              break;
            case 'server_error':
              friendlyMessage = t('errors.oauthServerError', 'The authentication service is temporarily unavailable. Please try again later.');
              break;
            default:
              friendlyMessage = t('errors.oauthGeneric', 'Sign-in failed. Please try again.');
          }

          console.error('OAuth error from URL params:', { error: errorParam, description: errorDescription });
          setError(friendlyMessage);

          // Redirect to home with error after a delay
          setTimeout(() => {
            navigate(Routes.HOME, { replace: true });
          }, 4000);
          return;
        }

        // Get the session from Supabase
        const { data, error } = await supabase.auth.getSession();

        if (error) {
          console.error('Auth callback error:', error);

          // Map Supabase auth errors to user-friendly messages
          let friendlyMessage = error.message;

          if (error.message.includes('email_not_confirmed')) {
            friendlyMessage = t('errors.emailNotConfirmed', 'Please check your email and click the confirmation link to complete sign-up.');
          } else if (error.message.includes('invalid_credentials')) {
            friendlyMessage = t('errors.invalidCredentials', 'Invalid credentials. Please try signing in again.');
          } else if (error.message.includes('too_many_requests')) {
            friendlyMessage = t('errors.rateLimited', 'Too many attempts. Please wait a few minutes and try again.');
          } else {
            friendlyMessage = t('errors.signInFailed', 'Sign-in failed. Please try again.');
          }

          setError(friendlyMessage);

          // Redirect to home with error after a delay
          setTimeout(() => {
            navigate(Routes.HOME, { replace: true });
          }, 4000);
          return;
        }

        if (data.session?.user) {
          // Successfully authenticated
          console.log('OAuth authentication successful:', {
            provider: data.session.user.app_metadata?.provider,
            userId: data.session.user.id,
            email: data.session.user.email
          });

          setSuccess(true);

          // Show success state briefly before redirecting
          setTimeout(() => {
            navigate(Routes.HOME, { replace: true });
          }, 2000);
        } else {
          // No session found - could be a refresh issue
          console.warn('No session found during OAuth callback');
          setError(t('errors.noSessionFound', 'Authentication session not found. Please try signing in again.'));

          setTimeout(() => {
            navigate(Routes.HOME, { replace: true });
          }, 3000);
        }
      } catch (err) {
        console.error('Unexpected error during auth callback:', err);
        setError(t('errors.signInFailed', 'An unexpected error occurred. Please try again.'));

        // Redirect to home after a delay
        setTimeout(() => {
          navigate(Routes.HOME, { replace: true });
        }, 4000);
      }
    };

    handleAuthCallback();
  }, [navigate, t, searchParams]);

  if (success) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <div className="max-w-md w-full space-y-8">
          <div className="text-center">
            <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-green-100 dark:bg-green-900/20">
              <span className="text-2xl">âœ…</span> {/* i18n-exempt: universal success symbol */}
            </div>
            <h2 className="mt-6 text-3xl font-extrabold text-gray-900 dark:text-white">
              {t('callback.success', 'Sign-in successful!')} {/* i18n-exempt: fallback text provided */}
            </h2>
            <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">
              {t('callback.successMessage', `Successfully signed in with ${provider}. Redirecting to your dashboard...`)} {/* i18n-exempt: fallback text provided */}
            </p>
            <div className="mt-4">
              <div className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400">
                {t('callback.redirectingShort', 'Redirecting...')} {/* i18n-exempt: fallback text provided */}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <div className="max-w-md w-full space-y-8">
          <div className="text-center">
            <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100 dark:bg-red-900/20">
              <span className="text-2xl">âŒ</span> {/* i18n-exempt: universal error symbol */}
            </div>
            <h2 className="mt-6 text-3xl font-extrabold text-gray-900 dark:text-white">
              {t('callback.error', 'Authentication failed')} {/* i18n-exempt: fallback text provided */}
            </h2>
            <p className="mt-2 text-sm text-gray-600 dark:text-gray-400 max-w-sm mx-auto">
              {error}
            </p>
            <div className="mt-6 space-y-3">
              <button
                onClick={() => navigate(Routes.HOME, { replace: true })}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors"
              >
                {t('callback.tryAgain', 'Try Again')} {/* i18n-exempt: fallback text provided */}
              </button>
              <p className="text-xs text-gray-500 dark:text-gray-400">
                {t('callback.autoRedirectIn', 'Auto-redirecting in a few seconds...')} {/* i18n-exempt: fallback text provided */}
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-blue-100 dark:bg-blue-900/20">
            <svg className="animate-spin h-6 w-6 text-blue-600 dark:text-blue-400" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
          </div>
          <h2 className="mt-6 text-3xl font-extrabold text-gray-900 dark:text-white">
            {t('callback.processing', 'Processing authentication...')} {/* i18n-exempt: fallback text provided */}
          </h2>
          <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">
            {t('callback.processingMessage', `Completing sign-in with ${provider}. This should only take a moment.`)} {/* i18n-exempt: fallback text provided */}
          </p>
          <div className="mt-4">
            <div className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-400">
              {t('callback.pleaseWait', 'Please wait...')} {/* i18n-exempt: fallback text provided */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthCallbackPage;
